INSERT INTO `days` (`id`, `tours_id`, `title`, `description`, `hotel`, `photo`) VALUES
(1, 1, 'JOUR 1 – De DENPASAR – à BLAYU : arrivée à Bali ', 'Accueil fleuri et personnalisé à l\'aéroport et transfert avec votre guide à JIMBARAN. \r\nA deux pas de la plage, vous pourrez vous reposer de votre voyage. ', 'Nuit dans un hôtel de charme de style traditionnel balinais, avec piscine. Très bon service.', 'jimbaran-beach-1.jpg'),
(2, 1, 'JOUR 2 – De BLAYU à PEMUTERAN : découverte de la campagne balinaise ', 'Visite du célèbre temple de TANAH LOT : dédié aux dieux de la mer, il est l’un des maillons de la chaîne des temples marins qui s’égrènent le long de la côte ouest. \r\n\r\nPuis route à travers les rizières en terrasses jusqu’au village de BLIMBLING et DÉJEUNER dans une auberge face à un superbe paysage. \r\nL’après-midi, PROMENADE à pied dans les plantations de café, girofliers, cacao, vanille, etc.\r\nPuis vous passerez par PUPUAN, une magnifique route plus montagneuse parmi les plantations de café, d’hortensias et de girofliers.', 'Nuit dans un hôtel au concept touristique unique : il est géré par les habitants de Mundunk. Il soutient la communauté dans la protection de l’environnement et la conservation de la culture, le tout dans un esprit du tourisme responsable.', 'tanah-lot-2.jpg'),
(3, 1, 'JOUR 3 – De MUNDUK à UBUD : la région des lacs', 'BALADE matinale avec votre guide et un guide local et découverte du village de Munduk et sa campagne environnante. \r\n\r\nPuis visites autour de Munduk : BEDUGUL, village de montagne au bord du lac BRATAN, où l’on peut voir le temple semi-aquatique d’ULUN DANU, consacré à la déesse des eaux, Dewi Danu. \r\n\r\nContinuation pour la région superbe de JATILUWIH avec ses rizières en terrasses à perte de vue. \r\nArrivée à Ubud, village d\'artistes du centre de l\'île.\r\n', 'Nuit dans un petit hôtel de charme avec un magnifique jardin entourant la piscine. Situé dans les environs d\'Ubud, dans un quartier calme entouré de rizières et tout près de la forêt des singes.', 'ulun-danu.jpg'),
(4, 1, 'JOUR 4 – UBUD : balade en vélo', 'Départ pour une balade en VELO à travers les rizières et les villages, et qu vous plongera dans l’atmosphère balinaise et la vie quotidienne des habitants.  \r\n\r\nRetour à Ubud, après-midi libre pour vous balader dans la ville et au grand marché. \r\n', 'Nuit à l’hôtel', 'ricefield-4.jpg'),
(5, 1, 'JOUR 5 – D’UBUD à SIDEMEN : une région authentique ', 'Départ pour l’Est de Bali et visite de GUNUNG KAWI, la « montagne des poèmes ». La base de la montagne a été taillée et sculptée, et elle abriten les monuments funéraires des premiers rois balinais. \r\n\r\nDécouverte de la source de SEBATU : depuis plus 1000 ans, le temple et ses deux bassins sont fréquentés par les balinais pour les pouvoirs curatifs de leur eau de source. Des cérémonies régulières y sont tenues pour la purification.\r\n\r\nEt enfin, visite du superbe PURA KEHEN, l’ancien temple du royaume de BANGLI, l’un des plus beaux de l’île avec ses autels ornés de très fines sculptures.\r\n\r\nEn fin d’après-midi, continuation pour Sidemen, village situé au milieu des rizières. \r\n', 'Nuit dans un hôtel de style balinais, avec une belle piscine et une vue époustouflante sur la vallée. Service attentionné, très bonne table et massages traditionnels. ', 'sebatu-2.jpg'),
(6, 1, 'JOUR 6 – SIDEMEN : découverte de la vie balinaise', 'Vous irez tôt sur le marché du village chercher les ingrédients nécessaires à votre cours de cuisine. \r\n\r\nPuis vous vous installerez dans la cuisine traditionnelle balinaise face au volcan Agung, pour votre COURS DE CUISINE avec le chef balinais.\r\nDEJEUNER avec les mets préparés. \r\n\r\nAprès-midi libre pour vous détendre à l’hôtel ou vous balader dans les environs. \r\n', 'Nuit à l’hôtel', 'cooking-class-2.jpg'),
(7, 1, 'JOUR 7 – SIDEMEN : visites et farniente', 'Départ le matin pour les bains royaux de TIRTAGGANGA, entourés de collines couvertes de rizières et de cultures en terrasses. \r\n\r\nPoursuite vers la belle plage de sable blanc de PASIR PUTIH où vous pourrez déguster un déjeuner de poissons grillés.\r\nAprès-midi baignade dans les eaux turquoise de l’océan indien.\r\n', 'Nuit à l’hôtel', 'water-palace.jpg'),
(8, 1, 'JOUR 8 – De SIDEMEN à NUSA LEMBONGAN', 'Départ pour le port où vous prendrez le Speedboat pour l’île de NUSA LEMBONGAN, petite île tranquille située à 30 min environ. ', 'Nuit dans un hôtel de charme, l’hôtel propose des petits bungalows en bois, restaurant et piscine avec vue sur la mer.', 'lembongan.jpg'),
(9, 1, 'JOURS 9 & 10 – NUSA LEMBONGAN : séjour plage', 'Séjour libre pour la baignade et la plongée.\r\n\r\nA faire : Selon vos envies, vous pourrez organiser des plongées, cours de surf, sorties Snorkelling en bateau autours de Nusa Lemobngan, visiter l’île voisine de Nusa Penida, prendre un cours de surf, etc. \r\n', 'Nuits à l’hôtel', 'lembongan-2.jpg'),
(10, 1, 'JOUR 11 – DÉPART', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert au port puis à l’aéroport et assistance à l’enregistrement.\r\n', NULL, 'airport-bali-1.jpg'),
(51, 3, 'JOUR 01 – AEROPORT DE JOGJAKARTA : arrivée à Java ', 'Accueil personnalisé à l\'aéroport de Jogjkarta, et transfert à l\'hôtel avec votre guide.\r\n\r\n', '\r\nNuit dans un petit hôtel de charme, en centre-ville. ', 'street-java-4.jpg'),
(52, 3, 'JOUR 02 – JOGJAKARTA : découverte de la ville', 'Excursion matinale vers le fameux complexe archéologique de BOROBUDUR, joyaux de l’art bouddhiste tant par la majesté de l’architecture que par la perfection des bas-reliefs. \r\nPuis visite des petits temples environnants : le CANDI PAWON et le CANDI MENDUT, dont les bas reliefs constituent l’un des plus brillants chefs-d’œuvre de l’art indo-javanais.\r\n\r\nEn fin d’après-midi, excursion au temple hindouiste de PRAMBANAN, classé au Patrimoine mondial de l’Unesco, il est un sanctuaire de l’hindouisme riche de plus de 200 templions. \r\n', 'Nuit dans un petit hôtel de charme, en centre ville. ', 'prambanan-2.jpg'),
(53, 3, 'JOUR 3 – De JOGJAKARTA à SURABAYA : début de la traversée', 'Visite du fameux complexe archéologique de BOROBUDUR : joyaux de l’art bouddhiste tant par la majesté de l’architecture que par la perfection des bas-reliefs. \r\n\r\nDans l\'après-midi transfert à la gare et embarquement à bord d’un TRAIN en 1ère classse pour rejoindre la ville de SURABAYA. \r\n', 'Nuit dans un hôtel javanais avec restaurant et piscine.', 'borobudur5.jpg'),
(54, 3, 'JOUR 4– De SURABAYA à BROMO : vie quotidienne javanaise', 'VISITE de la VEILLE VILLE de Surabaya avec ses marchés et ses lieux animés, vous pourrez observer la vie traditionnelle des Indonésiens. \r\n\r\nPuis départ pour le Mont Bromo, la route traverse les plantations et les paysages magnifiques. Arrivée dans un petit village, à une altitude de 1700 m.\r\n', 'Nuit dans un hôtel simple mais très bien situé pour le départ de l’ascension du lendemain. ', 'street-java.jpg'),
(55, 3, 'JOUR 5– De BROMO à KALIBARU : nature authentique', 'Tôt le matin, excursion au MONT PANANJAKAN pour admirer le lever du soleil sur le Mont Bromo et toute la chaîne de volcans de Java Est, puis traversée de la mer de sable et ascension des marches jusqu’au cratère du volcan. \r\n\r\nPuis départ pour la région des parcs nationaux de Java Est. \r\n', 'Nuit dans un « hôtel à la ferme » typiquement javanais, très convivial, situé dans les plantations.', 'bromo4.jpg'),
(56, 3, 'JOUR 6– De KALIBARU à IJEN : dernière étape à Java', 'PROMENADE à pied dans les plantations autour de l’hôtel pour découvrir les plantes locales, et visite du village où il fait bon flâner. \r\n\r\nDépart pour la petite ville côtière de Banyuwangi\r\n', 'Nuit dans un hôtel qui possède piscine et restaurant, dans un grand et beau jardin. ', 'landscape-java.jpg'),
(57, 3, 'JOUR 7– D’IJEN à PEMUTERAN : volcan, rencontres et arrivée à Bali', 'Tôt le matin, randonnée au volcan KAWAH IJEN.\r\n\r\nPerspective imprenable, couleurs qui se dévoilent et paysages montagneux. L’IJEN, c’est également les porteurs de soufre, au quotidien bien loin du nôtre !\r\n\r\nRedescente, transfert au port de Ketapang et traversée en ferry pour Bali, puis route vers le petit village de Pemuteran. \r\n\r\nArrivée au port de GILIMANUK et continuation en voiture …\r\n', 'Nuit dans un hôtel au grand charme situé sur la plage. L’hôtel a été créé dans le respect de l’environnement et la plus pure tradition balinaise. ', 'ijen-3.jpg'),
(58, 3, 'JOURS 8 & 9 – PEMUTERAN : farniente', 'Séjour libre pour profiter de la plage et de l’hôtel. \r\n\r\nA faire : sortie Snorkelling ou plongée à Menjangan, balade à pied ou en VTT dans le parc national à la rencontre des animaux, kayak, etc. \r\n', 'Nuits à l’hôtel', 'pemuteran.jpg'),
(59, 3, 'JOURS 10 – De PEMUTERAN à UBUD : paysages et culture balinaise', 'Baignade aux sources chaude de BANJAR au milieu d’un jardin luxuriant et d’une jungle accueillante ; et visite du temple bouddhiste de Banjar.\r\n\r\nRoute vers Munduk, visite de BEDUGUL, village de montagne au bord du lac BRATAN, et du temple semi-aquatique d’ULUN DANU.\r\n\r\nBalade dans la région superbe de JATILUWIH avec ses rizières en terrasses à perte de vue. Arrivée à Ubud, village d\'artistes du centre de l\'île. \r\n', 'Nuit dans un petit hôtel de charme dans les rizières. Une adresse intimiste et chaleureuse. La piscine donne directement sur les rizières, une pause nature à savourer !', 'ulun-danu-3.jpg'),
(60, 3, 'JOUR 11 – UBUD : découverte de la ville', 'JOURNEE LIBRE pour faire découvrir avec la capitale culturelle de l’île, se perdre dans les nombreuses galeries d\'art, visiter le marché, etc. ', 'Nuit à l’hôtel.', 'ubud-monkey-forest.jpg'),
(61, 3, 'JOUR 12 – D’UBUD à SIDEMEN : l’Est, région authentique', 'Départ pour l’Est de Bali, visite du site de GUNUNG KAWI, la « montagne des poèmes ». La base de la montagne a été taillée et sculptée, des niches ovales les monuments funéraires des premiers rois balinais. \r\n\r\nPuis découverte de la source sacrée de SEBATU. Depuis plus 1000 ans, le temple et ses deux bassins sont fréquentés par les balinais pour les pouvoirs curatifs de leur eau de source. Des cérémonies régulières y sont tenues pour la purification.\r\n\r\nDépart pour une BALADE en VÉLO à travers les rizières et les villages. Un très beau parcours qui vous permettra de plonger dans l’atmosphère balinaise \r\n', 'Nuit dans un charmant hôtel situé au milieu des rizières, avec piscine et restaurant.', 'sebatu-1.jpg'),
(62, 3, 'JOUR 13 – SIDEMEN : la magie balinaise', 'Journée libre pour profiter du cadre privilégié et de la magie balinaise.', 'Nuit à l’hôtel', 'sidemen-3.jpg'),
(63, 3, 'JOUR 14 – DÉPART', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert à l’aéroport et assistance à l’enregistrement.\r\n\r\n', NULL, 'airport-bali-2.jpg'),
(101, 4, 'JOUR 1 – De MAKASSAR à RANTEPAO : arrivée à Sulawesi', 'Arrivée à l’aéroport de MAKASSAR, capitale des Célèbes appelée aussi SULAWESI. \r\n\r\nAccueil par votre guide et route pour le pays Toraja. Vous traverserez une région luxuriante et montagneuse. En route, visite d’une maison traditionnelle et déjeuner de poissons et fruits de mer dans un restaurant local en bord de mer. \r\n\r\nArrivée à RANTEPAO en fin de journée. \r\n', 'Nuit dans un hôtel de style Toraja, avec de grands jardins, piscine et restaurant.', 'makassar.jpg'),
(102, 4, 'JOUR 2 – RANTEPAO : le pays Toraja', 'Journée de découverte du fascinant pays TORAJA : organisée par le guide local en fonction des différentes cérémonies auxquelles il vous fera participer. \r\n\r\nLes Torajas vivent dans des maisons en forme de bateaux et vouent un culte au buffle d’eau. \r\nIls organisent de grandioses cérémonies funéraires qui durent plusieurs jours, et leurs systèmes de tombes suspendues et installées dans les falaises sont célèbres. \r\n\r\nDécouvertes : Lemo, la falaise et les grottes funéraires avec une impressionnante série de balcons aux effigies mortuaires, les Tau Tau ; le village de Kete Kesu, véritable chef d’œuvre d’architecture et d’harmonie colorée, etc. \r\n', 'Nuit à l’hôtel', 'rentepao.jpg'),
(103, 4, 'JOUR 3 – De RANTEPAO à BATUTUMONGA : vers la montagne', 'Départ à pied pour une belle randonnée à travers une forêt de bambou qui vous emmènera au village de montagne de Batutumonga.\r\n\r\nVisites à Lokomata où les tombeaux des habitants du village ont été creusés dans une roche géante ! \r\nDécouverte des villages, de la vie quotidienne et des coutumes locales. \r\n', 'Nuit dans une maisons traditionnelle Toraja dominant la vallée.', 'toraja-2.jpg'),
(104, 4, 'JOUR 4 – De BATUTUMONGA à RANTEPAO : immersion chez les Torajas', 'Belle redescente à pied à travers les villages et les rizières. \r\nDe retour à Rantepao, poursuite des visites dans le pays TORAJA toujours en fonction des cérémonies et événements organisés. \r\n\r\n', 'Nuit dans un hôtel de style Toraja, avec de grands jardins, piscine et restaurant.', 'toraja-19.jpg'),
(105, 4, 'JOUR 5 – De RANTEPAO à SENGKANG : le lac Tempe', 'Départ vers le sud par la route côtière jusqu’au village lacustre de SENGKANG, au bord du lac Tempe.\r\n\r\nBalade sur longue barque motorisée pour découvrir les richesses du Lac Tempe : les maisons flottantes, ses habitants leurs activités sur les berges. \r\nLe retour au coucher du soleil est mémorable !\r\n', 'Nuit dans un hôtel simple mais agréable, et la meilleure option de la région.', 'mountain-sulawesi.jpg'),
(106, 4, 'JOUR 6 – De SENGKANG à BIRA : découverte des plages', 'Départ par la route sur la côte ouest de l’île et visite de chantiers de construction navale à TANABERU, réputés pour fournir les meilleurs bateaux en bois d’Indonésie. Les charpentiers utilisent toujours des techniques ancestrales sans aucun plan de construction.\r\n\r\nDéjeuner en route dans un restaurant local, en fin d’après-midi, arrivée sur la superbe plage de sable blanc de BIRA, au sud de SULAWESI.\r\n\r\n', 'Nuit dans un hôtel en bord de plage. ', 'boat.jpg'),
(107, 4, 'JOURS 7 & 8 – BIRA : séjour farniente', 'Séjour libre pour la baignade et le snorkeling sur cette belle plage préservée.\r\n\r\nA faire : sortie en bateau traditionnel vers l’île de Liukang et ses plages sauvages de sable blanc, ses eaux cristallines et vous pourrez y faire du snorkeling ou de la pêche.\r\n', 'Nuits à l’hôtel.', 'beach-sulawesi.jpg'),
(108, 4, 'JOUR 9 – DÉPART', 'Transfert à l’aéroport de Makassar et envol vers votre prochaine destination.', NULL, 'plane-1.jpg'),
(151, 5, 'JOUR 1 – LABUAN BAJO : arrivée à Flores', 'Arrivée à l’aéroport, accueil par votre guide et transfert au port. \r\n\r\nInstallation dans votre cabine et départ pour votre croisière dans le Parc National de Komodo. \r\n\r\nNavigation vers l’île de RINCA où vous débuterez votre randonnée. C\'est l\'occasion d\'admirer au fil de vos pas, la faune et la flore endémique de la région, dont les fameux dragons de Komodo dans leur habitat naturel. \r\n', 'Repas et Nuit à bord. ', 'komodo-8.jpg'),
(152, 5, 'JOUR 2 – LE PARC NATIONAL DE KOMODO', 'Navigation dans le magnifique Parc de Komodo. \r\nA PINK BEACH, magnifique plage colorée par le corail rose, temps libre pour la baignade et le Snorkeling. \r\n\r\nDÉJEUNER à bord.\r\n', 'Repas et Nuit à bord. ', 'komodo-7.jpg'),
(153, 5, 'JOUR 3 – PARC NATIONAL & LABUAN BAJO', 'Snorkeling à BIDADARI puis retour au port et transfert à votre hôtel, à Labuan Bajo\r\n', 'Nuit dans un petit hôtel en bord de plage. Très confortable, on y trouve restaurant et piscine, dans un grand jardin.', 'komodo-4.jpg'),
(154, 5, 'JOUR 4 – LABUAN BAJO', 'Journée libre à Labuan Bajo pour profiter de l’hôtel et de la petite bourgade.', 'Nuit à l’hôtel', 'labuan-bajo.jpg'),
(155, 5, 'JOUR 5 – DÉPART', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert à l’aéroport et assistance à l’enregistrement.\r\n', NULL, 'plane-5.jpg'),
(201, 6, 'JOUR 1 – De DE DENPASAR à SANUR : arrivée à Bali', 'Accueil fleuri et personnalisé à l\'aéroport et transfert avec votre guide à Sanur. ', 'Nuit dans un petit hôtel familial en bord de plage. ', 'sanur-2.jpg'),
(202, 6, 'JOUR 2 – De SANUR à BATUKARU : la campagne balinaise', 'Matinée libre pour vous reposer de votre voyage et profitez de la plage. \r\n\r\nPuis départ vers l’intérieur des terres, à travers les villages et les paysages de rizières.\r\n\r\nVisite du temple TAMAN AYUN, entouré de larges douves, il séduit le visiteur par le calme, l’équilibre des bâtiments et la richesse des sculptures. \r\n', 'Nuit dans un Ecolodge situé sur les pentes du volcan Batukaru, au cœur de la forêt tropicale. \r\nEn harmonie avec la nature, l’hôtel propose des bungalows en bois intégrés dans la forêt.\r\n', 'taman-ayun.JPG'),
(203, 6, 'JOUR 3 – BATUKARU : au fond de la jungle', 'Départ pour une RANDONNÉE de 5 heures dans la jungle sur les pentes du volcan BATUKARU.\r\nVous découvrirez la faune et la flore endémique, dont de nombreuses espèces en voie de disparition.\r\n\r\nAprès-midi libre pour le farniente dans ce site privilégié.\r\n\r\nA faire : de nombreuses activités pour découvrir la culture balinaise (cours de cuisine, calligraphie, fabrication d’offrandes, atelier sur les plantes médicinales, le travail du bois, etc.)', 'Nuit à l’hôtel', 'tabanan-waterfall.jpg'),
(204, 6, 'JOUR 4 – De BATUKARU à MUNDUK : route vers le nord', 'Tôt le matin, cours de yoga sur la plateforme en bambou flottant au-dessus des arbres. \r\n\r\nPuis route vers PUPUAN, qui serpente entre plantations, rizières et villages encore paisibles. \r\nArrêt à la cascade de PUJUNGAN où vous pourrez vous baigner puis visite du MONASTÈRE BOUDDISTE de BANJAR, l’un des plus anciens de Bali. \r\n\r\nDépart pour le village de MUNDUK entourée de végétation luxuriante.', 'Nuit dans une maison d\'hôtes : une expérience immersive !', 'ricefield-4.jpg'),
(205, 6, 'JOUR 5 – De MUNDUK à UBUD ', 'BALADE à la découverte du village de Munduk et sa campagne environnante. \r\n\r\nDirection la région des lacs et le village de montagne BEDUGUL. \r\nContinuation par la visite du temple semi-aquatique d’ULUN DANU situé sur le lac BRATAN, consacré à la déesse des eaux, Dewi Danu. \r\n\r\nBalade à pied à JATILUWIH, dont les magnifiques rizières en terrasses, fruits du système unique d’irrigation mis en place à Bali et reconnu par l’UNESCO, s’étendent à perte de vue.\r\n\r\nArrivée à UBUD, la capitale culturelle de l’île.', 'Installation dans un petit de charme tenu par une famille balinaise. \r\nOn traverse la charmante maison familiale traditionnelle pour arriver aux chambres et à la piscine.', 'ulun-danu.jpg'),
(206, 6, 'JOUR 6 – UBUD : découverte de la ville', 'Journée libre à Ubud pour faire connaissance avec la capitale culturelle de l’île : les musées et galeries d\'art, les boutiques d’artisanat, le grand marché, les spectacles de danses traditionnelles, etc. ', 'Nuit à l’hôtel', 'dragon.jpg'),
(207, 6, 'JOUR 7 – De UBUD à SEBATU : immersion dans la vie balinaise', 'Départ pour l’Est de Bali et visite de GUNUNG KAWI, la « montagne des poèmes ». La base de la montagne a été taillée et sculptée, et elle abriten les monuments funéraires des premiers rois balinais. \r\n\r\nDécouverte de la source de SEBATU : depuis plus 1000 ans, le temple et ses deux bassins sont fréquentés par les balinais pour les pouvoirs curatifs de leur eau de source. Des cérémonies régulières y sont tenues pour la purification.\r\n\r\nVous rencontrerez vos hôtes et ils vous emmèneront en balade jusqu’aux bains sacrés de SEBATU. \r\nDepuis plus 1000 ans, le temple et ses deux bassins sont fréquentés par les balinais pour les pouvoirs curatifs de leur eau de source. Des cérémonies régulières y sont tenues pour la purification.\r\nVous participerez avec eux à une cérémonie rituelle de purification par l’eau sacrée.\r\n', 'Nuit chez l’habitant', 'temple-offering.jpg'),
(208, 6, 'JOUR 8 – De SEBATU à SIDEMEN : une région authentique', 'Visite du superbe PURA KEHEN, l’ancien temple du royaume de BANGLI, l’un des plus beaux de l’île avec ses autels ornés de très fines sculptures.\r\n\r\nPuis BALADE à VÉLO depuis le volcan Batur, à travers les rizières et les villages.\r\nUn très beau parcours, sur une descente en pente douce, qui vous permettra de plonger dans l’atmosphère balinaise. \r\nArrivée en vélo à la petite ville de KLUNGKUNG, dont vous découvrirez le marché.\r\n\r\nA Sidemen, vous visiterez une école d’activités balinaises accueillant chaque jour les enfants du village pour leur enseigner la danse, le gamelan (musique traditionnelle), la méditation, etc. ', 'Nuit dans un hôtel au grand charme entouré de rizières pour profiter de la magie balinaise ! ', 'agung-2.jpg'),
(209, 6, 'JOUR 9 – SIDEMEN : slow life ', 'CEREMONIE matinale au temple de l’hôtel : les balinais vont prier au temple plusieurs fois par jour. Vous partagerez leurs rites et traditions le temps d’une prière aux dieux de la nature. \r\n\r\nDÉJEUNER INSOLITE chez notre ami chef de cuisine, il vous préparera un menu dégustation dans son jardin. \r\n\r\nL’après-midi, découverte du SIDEMEN « SECRET » : panorama sur les rizières et le volcan AGUNG ; visite d’une maison traditionnelle, où vous prendrez le thé et rencontrerez les habitants ; découverte du processus de fabrication de l’Arak, l’alcool local et du café balinais ; etc.\r\n', 'CEREMONIE matinale au temple de l’hôtel : les balinais vont prier au temple plusieurs fois par jour. Vous partagerez leurs rites et traditions le temps d’une prière aux dieux de la nature. \r\n\r\nDÉJEUNER INSOLITE chez notre ami chef de cuisine, il vous préparera un menu dégustation dans son jardin. \r\n\r\nL’après-midi, découverte du SIDEMEN « SECRET » : panorama sur les rizières et le volcan AGUNG ; visite d’une maison traditionnelle, où vous prendrez le thé et rencontrerez les habitants ; découverte du processus de fabrication de l’Arak, l’alcool local et du café balinais ; etc.\r\nNuit à l’hôtel\r\n', 'ceremony.jpg'),
(210, 6, 'JOUR 10 – SIDEMEN : à la plage', 'Visite du palais aquatique de TAMAN UJUNG, le palais d’été du dernier roi de Karangasem, entouré de bassins et situé au cœur d’un parc paisible.\r\n\r\nPuis route vers le village d’AMED où vous pourrez vous baigner et profiter de la plage.\r\n\r\nVous pourrez aussi découvrir l’association pour la protection de l’environnement PEDULI ALAM. \r\nL’association aide les villageois à prendre conscience de l’impact sur la nature et la santé de l’incinération et du rejet des déchets dans la nature ; elle organise aussi la collecte des déchets ménagers.', 'Nuit à l’hôtel', 'amed.jpg'),
(211, 6, 'JOUR 11 – SIDEMEN ', 'JOURNÉE LIBRE pour profiter de ce cadre idyllique. \r\n\r\nVous profiterez d’un MASSAGE traditionnel balinais. ', 'Nuit à l’hôtel', 'sidemen-1.jpg'),
(213, 6, 'JOUR 13 – DÉPART', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert à l’aéroport et assistance à l’enregistrement.', NULL, 'plane-1.jpg'),
(251, 7, 'JOUR 1 – De DENPASAR – à UBUD : arrivée à Bali ', 'Accueil fleuri et personnalisé à l\'aéroport et transfert avec votre guide à UBUD, la capitale de l’île. ', 'Nuit dans un petit hôtel de charme avec une belle décoration raffinée, proche du centre d\'Ubud.', 'ubud-3.jpg'),
(252, 7, 'JOUR 2 – UBUD : journée libre', 'Journée libre à Ubud pour faire connaissance avec la capitale culturelle de l’île : se balader dans la forêt des singes, découvrir les musées et galeries d\'art, assister à un spectacle de danse, goûter à la gastronomie locale, etc. ', 'Nuit à l’hôtel', 'children-2.jpg'),
(253, 7, 'JOUR 3 – UBUD : balade à la campagne', 'Départ pour une balade en VELO à travers les rizières et les villages, et qu vous plongera dans l’atmosphère balinaise et la vie quotidienne des habitants.  \r\n\r\nDéjeuner dans le palais d’été d’un prince balinais.\r\n\r\nVisite du célèbre temple de TANAH LOT : dédié aux dieux de la mer, il est l’un des maillons de la chaîne des temples marins qui s’égrènent le long de la côte ouest. \r\nPuis balade dans la forêt des singes avant de rentrer à Ubud.', 'Nuit à l’hôtel', 'tanah-lot.jpg'),
(254, 7, 'JOUR 4 – D’UBUD à MUNDUK : la région des lacs', 'Balade à pied dans la superbe région de JATILUWIH : elle offre de magnifiques rizières en terrasses à perte de vue.\r\n\r\nDécouverte de la région des lacs : BRATAN, BUYAN et TAMBLINGAN et visite du temple semi-aquatique d’ULUN DANU, consacré à la déesse des eaux, Dewi Danu. \r\n\r\nPuis parcours dans les arbres pour toute la famille, un circuit d’aventure situé à l’intérieur du beau jardin botanique de BEDUGUL. ', 'Nuit dans un hôtel au concept touristique unique : il est géré par les habitants de Munduk. Il soutient la communauté dans la protection de l’environnement et la conservation de la culture, le tout dans un esprit du tourisme responsable. ', 'jatiluwih.jpg'),
(255, 7, 'JOUR 5 – De MUNDUK à PEMUTERAN : le nord de l’île', 'BALADE matinale avec votre guide et un guide local et découverte du village de Munduk et sa campagne environnante. \r\n\r\nArrêt aux SOURCES d’eau chaude de Banjar situées au milieu d’un jardin luxuriant puis route vers Pemuteran. Visite du temple de PULAKI pour saluer les singes crabiers et arrêt au marché coloré de SERIRIT. \r\n', 'Nuit dans un hôtel au grand charme sur la plage de sable noir. Créé dans le respect de l’environnement et la plus pure tradition balinaise, il offre frd bungalows balinais situé dans un grand jardin.', 'ubud-monkey-forest.jpg'),
(256, 7, 'JOURS 6 & 7 – PEMUTERAN : farniente à la plage', 'Séjour libre pour profiter de la plage et de l’hôtel. \r\n\r\nA faire : sorties plongée ou Snorkelling au Parc National de Menjangan, randonnée et observation des animaux, sortie Kayak, etc.\r\n', 'Nuits à l’hôtel', 'pemuteran-biorock.jpg'),
(258, 7, 'JOUR 8 – De PEMUTERAN à SIDEMEN : temples, lacs & volcans', 'Au village de SANGSIT, visite du temple de BEJI construit au XVe siècle et dédié à la déesse des rizières, Dewi Sri. \r\n\r\nContinuation en longeant le LAC BATUR vers le village de montagne de KINTAMANI dominant le volcan et arrivée au village de Sidemen, situé au milieu des rizières. \r\n', 'Nuit dans un hôtel de charme dans le style balinais, situé sur 1 hectare de jardins, avec une belle piscine et une vue époustouflante sur la vallée. Service attentionné, très bonne table. ', 'agung-2.jpg'),
(259, 7, 'JOUR 9 – SIDEMEN : immersion balinaise', 'Vous irez tôt sur le marché du village chercher les ingrédients nécessaires à votre cours de cuisine. \r\n\r\nPuis vous vous installerez dans la cuisine traditionnelle balinaise face au volcan Agung, pour votre COURS DE CUISINE avec le chef balinais.\r\nDEJEUNER avec les mets préparés. \r\n\r\nDans l’après-midi, COURS D’ARTS BALINAIS : danse, musique (gamelan), toute la famille pourra s’initier à la fabrique des offrandes traditionnelles de temples et aux danses traditionnelles.\r\n', 'Nuit à l’hôtel ', 'sidemen-cooking.jpg'),
(260, 7, 'JOUR 10 – SIDEMEN : sortie rafting', 'RAFTING sur la rivière Telaga Waja : à travers une végétation sauvage et luxuriante, vous partez pour une expérience vivifiante et accessible à tous.', 'Nuit à l’hôtel ', 'sidemen-river.jpg'),
(261, 7, 'JOUR 11 – DÉPART', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert à l’aéroport et assistance à l’enregistrement.', NULL, 'airport-bali-5.jpg'),
(301, 8, 'JOUR 1 – MEDAN : arrivée à Sumatra', 'Accueil personnalisé à l\'aéroport de Medan et transfert avec votre guide à votre hôtel. ', 'Nuit dans un hôtel bien situé pour les visites du lendemain, avec piscine et restaurant. ', 'medan.jpg'),
(302, 8, 'JOUR 2 – De MEDAN à TANGKAHAN ', 'Visite de la ville de Medan : une ville asiatique, bruyante et chaotique comme on les aime ! Les touristes ne s’y attardent en pas en général mais la balade en vaut la peine : Palais du sultan de Deli, la grande Mosquée, le temple chinois, la villa Tjong A Fie, ainsi que le quartier indien.\r\n\r\nPuis départ en voiture Tangkahan dans le nord de Medan, dans le Parc National de Leuser. \r\nArrêts en cours de route pour voir les activités des habitants, des plantations et les villages. ', 'Nuit dans un lodge simple mais accueillant, au cœur du Parc National', 'sumatra-house-3.jpg'),
(303, 8, 'JOUR 3 – TANGKAHAN ', 'Journée découvertes et exploration du du Parc National : le centre de protection des éléphants, vous les verrez peut être prendre leur bain dans la rivière ; descente de la rivière sur des pneus ; balade dans la jungle et baignade dans les cascades. \r\n\r\nDéjeuner pique-nique dans cette nature luxuriante. \r\n\r\nPuis de retour au village, temps libre pour vous balader lilbrement et rencontrer les habitants. \r\n', 'Nuit au lodge', 'orang-outang-2.jpg'),
(304, 8, 'JOUR 4 – De TANGKAHAN à BUKIT LAWANG', 'Départ pour Bukit Lawang en jeep 4x4 avec quelques arrêts en cours de route aux plantations d’hévéa, de palmiers à huile et de forêts tropicales. Découverte des villages et de leur mode de vie traditionnel.\r\n\r\nA Bukit Lawang, randonnée dans la jungle, accompagné par un ranger, pour observer les orangs-outans, et découvrir la flore et la faune locale.\r\n', 'Nuit dans un écolodge', 'waterfall-sumatra.jpg'),
(305, 8, 'JOUR 5 – De BUKIT LAWANG à l’ILE DE SAMOSIR', 'Départ vers BRASTAGI, arrêt à la rivière SEMBAHE et aux cascades de SIBOLANGIT, arrivée à Brastagi et visite du marché aux fruits. \r\n\r\nPanorama sur la colline de GUNDALING, d’où l’on peut voir les fameux volcans SINABUNG et SIBAYAK, toujours en activité.\r\nVisite du Parc de Lhumbini, du marché traditionnel. \r\nVisite du VILLAGE TRADITIONNEL BATAK de Dokan, arrêt aux CHUTES D’EAU de Sipiso-piso, arrêt à la LONGHOUSE Rumah Bolon, et enfin à SIMARJARUNPUNG pour admirer la magnifique vue sur le lac. \r\n\r\nTraversée en FERRY depuis PARAPAT jusqu’à l’île de Samosir, au milieu de LAC TOBA.\r\n', 'Nuit dans un petit hôtel de style traditionnel', 'lac-toba-2.jpg'),
(306, 8, 'JOUR 6 – L’ILE DE SAMOSIR', 'Après le petit déjeuner, excursion pour découvrir les lieux les plus importants et la vie des habitants de SAMOSIR : AMBARITA, où siégeait les rois Sialagan ; SIMANINDO, son musée et ses spectacles de danses traditionnelles ; LAKE SIDHONI ; MENARA TELE ; TOMOK, sites funéraires des rois Sidabutar.', 'Nuit dans un petit hôtel ', 'lac-toba-4.jpg'),
(307, 8, 'JOUR 7 – DÉPART', 'Après le petit-déjeuner, longue route vers Medan, plusieurs arrêts sont prévus. \r\nL’après-midi, envol depuis l’aéroport de Medan. ', NULL, 'plane-1.jpg'),
(351, 9, 'JOUR 1 – De DENPASAR – à JIMBARAN : arrivée à Bali ', 'Accueil fleuri et personnalisé à l\'aéroport et transfert avec votre guide à JIMBARAN. ', 'Nuit dans un hôtel de charme de style traditionnel balinais, avec piscine. Très bon service. ', 'jimbaran-beach-2.jpg'),
(352, 9, 'JOUR 02 – JIMBARAN : détente à la plage', 'JOURNÉE LIBRE pour profiter de la belle plage calme de Jimbaran. \r\n\r\nUn massage traditionnel balinais vous sera prodigué au spa de l’hôtel. \r\n', 'Nuit à l’hôtel', 'massage-bali-2.jpg'),
(353, 9, 'JOUR 03 – JIMBARAN – UBUD : balade dans les campagnes', 'Découverte de la campagne à PENEBEL, BALADE à pied à travers les rizières et les villages. \r\nUne très belle promenade qui vous permettra de plonger dans l’atmosphère balinaise. \r\n\r\nPuis visite du temple TAMAN AYUN, il séduit le visiteur par le calme, l’équilibre des bâtiments et la richesse des sculptures.', 'Nuit dans un petit hôtel charmant avec une belle vue sur les rizières, piscine à débordement et restaurant. Très bonne table.', 'ricefield-4.jpg'),
(354, 9, 'JOUR 04 – UBUD : la capitale culturelle', 'Journée libre à Ubud pour faire connaissance avec la capitale culturelle de l’île : se balader dans la forêt des singes, découvrir les musées et galeries d\'art, assister à un spectacle de danse, goûter à la gastronomie locale, etc. \r\n\r\nUn MASSAGE traditionnel balinais d’vous sera prodigué au spa de l’hôtel. \r\n', 'Nuit à l’hôtel', 'massage-bali.jpg'),
(355, 9, 'JOUR 05 – UBUD – SIDEMEN : temples et purification', 'Départ pour l’Est de Bali et visite de GUNUNG KAWI, la « montagne des poèmes ». La base de la montagne a été taillée et sculptée, et elle abriten les monuments funéraires des premiers rois balinais. \r\n\r\nDécouverte de la source de SEBATU : depuis plus 1000 ans, le temple et ses deux bassins sont fréquentés par les balinais pour les pouvoirs curatifs de leur eau de source. \r\n\r\nVous participerez à une cérémonie rituelle de purification par l’eau sacrée.\r\n', 'Nuit dans un hôtel au grand charme, ambiance raffinée et intimiste, service attentionné, très bonne table, vue magnifique sur la vallée et le volcan. ', 'temple-offering.jpg'),
(356, 9, 'JOUR 06 – SIDEMEN : immersion balinaise', 'Vous irez tôt sur le marché du village chercher les ingrédients nécessaires à votre cours de cuisine. \r\n\r\nPuis vous vous installerez dans la cuisine traditionnelle balinaise face au volcan Agung, pour votre COURS DE CUISINE avec le chef balinais.\r\nDEJEUNER avec les mets préparés. \r\n\r\nDans l’après-midi, un MASSAGE traditionnel balinais vous sera prodigué au spa de l’hôtel.\r\n', 'Nuit à l’hôtel', 'cooking-class-3.jpg'),
(357, 9, 'JOUR 07 – SIDEMEN : randonnée et rizières', 'Séance de YOGA avec votre professeur, sur la terrasse dominant les rizières, en admirant le jour se lever sur la vallée. \r\n\r\nPuis départ pour une promenade à pied jusqu’au temple en haut de la colline, à travers les rizières et les villages. \r\nRetour à l’hôtel et baignade dans les rizières.\r\n', 'Nuit à l’hôtel', 'sidemen-trek.jpg'),
(358, 9, 'JOUR 08 – SIDEMEN : détente', 'Séance de YOGA & MÉDITATION avec votre professeur puis départ pour le village, où vous vous rendrez dans la maison d’un GUÉRISSEUR qui pratique la réflexologie plantaire.\r\n\r\nDans l’après-midi, MASSAGE DÉTENTE DU VISAGE au spa. \r\n', 'Nuit à l’hôtel', 'meditation-2.jpg'),
(359, 9, 'JOUR 09 – SIDEMEN : cérémonie et traditions', 'Petite CEREMONIE matinale au temple de l’hôtel : les balinais vont prier au temple plusieurs fois par jour. Ce moment vous permettra de partager leurs rites et traditions le temps d’une prière aux dieux de la nature. \r\n\r\nL’après-midi, rendez-vous au spa pour un soin traditionnel très complet appelé LULUR.\r\n', 'Nuit à l’hôtel', 'ceremony-2.jpg'),
(360, 9, 'JOUR 10 – DÉPART', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert à l’aéroport et assistance à l’enregistrement.\r\n', NULL, 'plane-5.jpg'),
(401, 10, 'JOUR 1 – De DENPASAR – à TABANAN : arrivée à Bali ', 'Accueil fleuri et personnalisé à l\'aéroport et transfert avec votre guide à JIMBARAN. \r\nA deux pas de la plage, vous pourrez vous reposer de votre voyage. \r\n', 'Nuit dans un petit hôtel de style balinais situé à Mengwi : une belle halte au cœur des rizières. Beaucoup de cachet dans un écrin dépaysant et paisible, avec piscine et restaurant.', 'ricefield-5.jpg'),
(402, 10, 'JOUR 2 – TABANAN : balade balinaise', 'Balade à VELO à travers les rizières et les villages. Une très belle promenade qui vous permettra de plonger dans l’atmosphère balinaise. \r\n\r\nEnsuite, visite du célèbre temple de TANAH LOT : dédié aux dieux de la mer, il est l’un des maillons de la chaîne des temples marins qui s’égrènent le long de la côte ouest. \r\n', 'Nuit à l’hôtel ', 'tanah-lot-2.jpg'),
(403, 10, 'JOUR 3 – De TABANAN à MUNDUK : découverte de la campagne balinaise ', 'Visite du temple TAMAN AYUN, élevé au milieu du XVIIe siècle par la famille royale de Mengwi. \r\n\r\nPuis route pour le Nord de l’île, vous prendrez les petites routes de rizières en terrasses jusqu’au village de BLIMBLING. PROMENADE à pied dans les plantations de café, girofliers, cacao, vanille, etc.\r\n\r\nDÉJEUNER dans une auberge balinaise face à un superbe paysage. \r\n\r\nEnsuite, départ pour les sources d\'eau chaude de BANJAR au milieu d’un jardin luxuriant et d’une jungle accueillante.\r\n', 'Nuit dans un hôtel au concept touristique unique : il est géré par les habitants de Mundunk. Il soutient la communauté dans la protection de l’environnement et la conservation de la culture, le tout dans un esprit du tourisme responsable. ', 'taman-ayun-2.JPG'),
(404, 10, 'JOUR 4 – De MUNDUK à UBUD : la région des lacs', 'BALADE matinale avec votre guide et un guide local et découverte du village de Munduk et sa campagne environnante. \r\n\r\nPuis visites autour de Munduk : BEDUGUL, village de montagne au bord du lac BRATAN, où l’on peut voir le temple semi-aquatique d’ULUN DANU, consacré à la déesse des eaux, Dewi Danu. \r\n\r\nContinuation pour la région superbe de JATILUWIH avec ses rizières en terrasses à perte de vue. \r\nArrivée à Ubud, village d\'artistes du centre de l\'île.\r\n\r\n', 'Nuit dans un petit hôtel de charme avec un magnifique jardin entourant la piscine. Situé dans les environs d\'Ubud, dans un quartier calme entouré de rizières et tout près de la forêt des singes.', 'ulun-danu-3.jpg'),
(405, 10, 'JOUR 5 – UBUD : la capitale culturelle ', 'Journée libre pour vous balader dans la ville et au grand marché. ', 'Nuit à l’hôtel', 'mask.jpg'),
(406, 10, 'JOUR 6 – D’UBUD à SIDEMEN : une région authentique ', 'Départ pour l’Est de Bali et visite de GUNUNG KAWI, la « montagne des poèmes ». La base de la montagne a été taillée et sculptée, et elle abriten les monuments funéraires des premiers rois balinais. \r\n\r\nDécouverte de la source de SEBATU : depuis plus 1000 ans, le temple et ses deux bassins sont fréquentés par les balinais pour les pouvoirs curatifs de leur eau de source. Des cérémonies régulières y sont tenues pour la purification.\r\n\r\nEt enfin, visite du superbe PURA KEHEN, l’ancien temple du royaume de BANGLI, l’un des plus beaux de l’île avec ses autels ornés de très fines sculptures.\r\n\r\nEn fin d’après-midi, continuation pour Sidemen, village situé au milieu des rizières. \r\n\r\n', 'Nuit dans un hôtel de style balinais, avec une belle piscine et une vue époustouflante sur la vallée. Service attentionné, très bonne table et massages traditionnels. ', 'tirta-empul.jpg'),
(407, 10, 'JOUR 7 – SIDEMEN : découverte de la vie balinaise', 'Vous irez tôt sur le marché du village chercher les ingrédients nécessaires à votre cours de cuisine. \r\n\r\nPuis vous vous installerez dans la cuisine traditionnelle balinaise face au volcan Agung, pour votre COURS DE CUISINE avec le chef balinais.\r\nDEJEUNER avec les mets préparés. \r\n\r\nAprès-midi libre pour vous détendre à l’hôtel ou vous balader dans les environs. \r\n\r\nUn MASSAGE traditionnel balinais vous sera prodigué au spa. \r\n', 'Nuit à l’hôtel', 'sidemen-market.jpg'),
(408, 10, 'JOUR 8 – SIDEMEN : randonnée nature', 'Dans la matinée, départ pour une PROMENADE A PIED (3H environ aller – retour) à travers les rizières jusqu’au PURA BUKIT, le temple en haut de la colline. La dernière partie de la randonnée est une ascension de la colline à travers la jungle, sur un petit sentier (bonne condition physique requise). \r\n\r\nA l’arrivée, surprise ! … Un BRUNCH est préparé pour vous au pied du temple, dans ce site à couper le souffle, avec vue à 360 degrés sur la vallée. Retour à l’hôtel en début d’après-midi, en traversant 2 petites RIVIERES où vous pourrez vous rafraîchir.\r\n', 'Nuit à l’hôtel', 'sidemen-1.jpg'),
(409, 10, 'JOUR 9 – De SIDEMEN à Lombok : traversée en Speedboat', 'Départ pour le port où vous prendrez le Speedboat pour l’île de LOMBOK. ', 'Nuit dans un hôtel situé au coeur d\'un jardin fleuri et au bord de la jolie plage de sable noir de Stangi. Belle piscine à débordement et restaurant en front de mer.', 'senggigi-1.jpg'),
(410, 10, 'JOURS 10 & 11 – LOMBOK : détente à la plage', 'Séjour libre pour la baignade et la farniente.\r\n\r\nA faire : sortie plongée ou Snorkeling aux îles Gilis, visite des villages traditionnels Sasak, etc. \r\n', 'Nuits à l’hôtel', 'senggigi-2.jpg'),
(411, 10, 'JOUR 12 – DÉPART ', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert à l’aéroport de Lombok.\r\n', NULL, 'plane-4.jpg'),
(451, 11, 'JOUR 1 – D’ENDE à MONI', 'Arrivée à l\'aéroport de Ende, accueil par votre guide et départ pour MONI avec arrêt pour le panorama de rizières en terrasses de DETUSOKO. ', 'Dîner et nuit dans un eco-lodge. ', 'flores-landscape-2.jpg'),
(452, 11, 'JOUR 2 De MONI à BAJAWA', 'Tôt le matin, départ pour une balade sur le volcan KELIMUTU. \r\nVous découvrirez un magnifique panorama à 360° sur les 3 lacs du volcan qui changent de couleur sans que l\'on sache pourquoi.\r\n\r\nRoute pour Bajawa et arrêt à NANGAPANDA, la plage aux galets bleus. \r\nDéjeuner dans un restaurant local.\r\n\r\n', 'Dîner et nuit dans un petit hôtel.', 'kelimutu-flores.jpg'),
(453, 11, 'JOUR 3 – De BAJAWA à GURUSINA', 'Dans la matinée, visite de villages de LUBA et BENA, au pied du Mont Inerie, pour découvrir la culture clanique Ngada avec ses sculptures de bois et ses rites ancestraux.\r\nVisite des ateliers artisanaux où l’on fabrique les superbes ikat, tissage traditionnel, d\'une grande qualité. \r\n\r\nDépart à pied jusqu’au village de GURUSINA qui vous accueille pour la nuit, une fabuleuse opportunité de dormir chez l\'habitant dans un village traditionnel. Vous serez accueillis très chaleureusement et les villageois vous feront partager avec un grand plaisir leur mode de vie ancestral. \r\n', 'Dîner et Nuit au village', 'woman-flores.jpg'),
(454, 11, 'JOUR 4 – De GURUSINA à RUTENG', 'Faites vos adieux aux villageois et départ pour AIMERE pour voir la distillation de l’Arak et de l’alcool de palme. \r\n\r\nDÉJEUNER en route. \r\n\r\n', 'Dîner et nuit dans un petit hôtel.', 'flores-landscape-3.jpg'),
(455, 11, 'JOUR 5 – De RUTENG à LABUAN BAJO', 'Départ pour CANCAR pour admirer les fameuses rizières en forme de toile d\'araignées. \r\nDÉJEUNER en cours de route. \r\n', 'Nuit dans un petit hôtel en bord de plage. Très confortable, on y trouve restaurant et piscine, dans un grand jardin.', 'flores-landscape.jpg'),
(456, 11, 'JOUR 6 – LABUAN BAJO : journée farniente', 'Journée libre à Labuan Bajo pour profiter de l’hôtel et se balader dans la petite bourgade. ', 'Nuit à l’hôtel', 'komodo-3.jpg'),
(457, 11, 'JOUR 7 – PARC NATIONAL DE KOMODO : randonnée, dragons & Snorkeling', 'Départ pour le port, à bord d’un bateau traditionnel, vous atteindrez l’île de RINCA où vous débuterez votre randonnée. C\'est l\'occasion d\'admirer la faune et la flore endémique de la région, dont les fameux dragons de Komodo.\r\n\r\nNavigation à travers le très beau Parc National de Komodo et ses îles, et DÉJEUNER à bord.\r\n\r\nPrès de l’île de KELOR, arrêt pour la baignade et le Snorkeling.\r\nPuis retour à Labuan Bajo. \r\n', 'Nuit à l’hôtel', 'komodo-7.jpg'),
(458, 11, 'JOUR 8 – DÉPART', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert à l’aéroport de Labuan Bajo.\r\n', NULL, 'plane-2.jpg'),
(501, 12, 'JOUR 1 – TAMBOLAKA : Arrivée à Sumba', 'Arrivée à l’aéroport de Tambolaka, accueil par votre guide et transfert à l’hôtel. \r\n\r\nDans l’après-midi, visites du musée RUMAH BUDAYA, balade au marché local, au village de BONDU KAPUMBU, à plage de MANANGA…\r\n', 'Arrivée à l’aéroport de Tambolaka, accueil par votre guide et transfert à l’hôtel. \r\n\r\nDans l’après-midi, visites du musée RUMAH BUDAYA, balade au marché local, au village de BONDU KAPUMBU, à plage de MANANGA…\r\nNuit à l’hôtel, dans un petit bungalow, situé sur une belle plage sauvage. \r\n', 'airport-sumba.jpg'),
(502, 12, 'JOUR 2 – LA RÉGION KODI ', 'Départ pour la région de KODI, visites et découvertes des magnifiques paysages de l’île, les villages et l’habitat traditionnel : Villages de Tosi, Ranggabaki and Rotenggaro…\r\n\r\nDÉJEUNER pique-nique sur la plage de PERO.\r\n\r\nRetour à l’hôtel en début de soirée.  \r\n', 'Nuit à l’hôtel', 'pero-beach-2.jpg'),
(503, 12, 'JOUR 3 – ANAKALANG & MAMBORO ', 'Départ pour une nouvelle journée de découverte de l’île et des cultures locales. \r\nRoute vers la région de ANAKALANG : les tombes mégalithiques de PASUNGA, les maisons traditionnelles des ancêtres Marapu à LAI TARUNG, les tombeaux royaux au village de GALU BAKUL. \r\n\r\nPuis route vers les campagnes de la région de MAMBORO, visites et balades dans les villages pour rencontrer et découvrir la vie des habitants (Manuakalada, Wawarongu, etc). Vous pourrez profiter des magnifiques paysages verdoyants et les rizières en terrasses.\r\n\r\nDÉJEUNER pique-nique en cours de route. \r\n', 'Dîner et Nuit dans un hôtel de style local, situé dans la petite ville de Waikabubak. ', 'prai-ijin.jpg'),
(504, 12, 'JOUR 4 – WAIKABUBAK & LA CÔTE SUD ', 'Découverte de la très belle cascade de LAI POPU ; des villages de WAIGALLI, PAIGOLY et WAIWUANG. \r\n\r\nArrêt aux plage de WATU BELLA et de MAROSI. \r\n\r\nDÉJEUNER en cours de route.\r\n\r\nTransfert vers votre hôtel situé dans le sud de l’île. \r\n\r\n', 'Nuit dans un hôtel familial, situé sur une magnifique plage encore peu fréquentée. L’hôtel dispose d’une belle piscine et d’un service de restauration.  ', 'pantai-kerewe.JPG'),
(505, 12, 'JOURS 5 & 6 – LA CÔTE SUD : séjour libre', 'Séjour libre pour profiter de l’hôtel, de la plage, des balades aux alentours, etc. ', 'Nuits à l’hôtel', 'beach-sumba-2.jpg'),
(506, 12, 'JOUR 7 – De TAMBOLAKA à ENDE : Envol pour Flores', 'Transfert à l’aéroport de Tambolaka avec votre chauffeur. \r\n\r\nA votre arrivée à l’aéroport d’Ende, accueil par votre guide et route vers MONI. \r\n', 'Dîner et Nuit dans un Ecolodge', 'land-sumba.jpg'),
(507, 12, 'JOUR 8 – De MONI à RIUNG', 'Tôt le matin, départ pour une balade sur le volcan KELIMUTU. \r\nVous découvrirez un magnifique panorama à 360° sur les 3 lacs du volcan qui changent de couleur sans que l\'on sache pourquoi.\r\n\r\nRoute pour Bajawa et arrêt à NANGAPANDA, la plage aux galets bleus. \r\n\r\n', 'Dîner et Nuit dans un petit hôtel', 'flores-landscape-2.jpg'),
(508, 12, 'JOUR 9 – De RIUNG à BAJAWA', 'Matinée baignade et Snorkeling autour des 17 îles du magnifique parc marin de Riung.\r\nAprès le DÉJEUNER, départ pour Bajawa. \r\n\r\nEn route, arrêt aux sources d’eau chaude de MEGERUNDA et au village traditionnel de MASU. \r\n', 'Dîner et Nuit dans un petit hôtel', 'flores-beach.jpg'),
(509, 12, 'JOUR 10 – De BAJAWA à RUTENG', 'Visite du village de BENA, au pied du Mont Inerie, pour découvrir la culture clanique Ngada avec ses sculptures de bois et ses rites ancestraux.\r\nVisite des ateliers artisanaux où l’on fabrique les superbes ikat, tissage traditionnel, d\'une grande qualité. \r\n\r\nRoute vers AIMERE pour voir la distillation de l’Arak et de l’alcool de palme.\r\n\r\nContinuation vers Ruteng et découverte du lac RANAMESE, situé au milieu d\'une forêt de nuages. C\'est le rendez-vous de centaines d\'espèces d\'oiseaux.\r\n', 'Dîner et Nuit dans un eco-lodge', 'ricefield-6.jpg'),
(510, 12, 'JOUR 11 – De RUTENG à LABUAN BAJO ', 'A GOLO CURU pour admirer un superbe panorama à 360° sur la ville de Ruteng et les très belles rizières de la région. \r\nVisite du village traditionnel de PUTENG PU’U et ses habitats en pierres de forme ronde, typiques de ce village. \r\n\r\nArrêt à CANCAR pour admirer les fameuses rizières en forme de toile d\'araignées. \r\n\r\nContinuation vers Labuan Bajo. \r\n', 'Nuit dans un petit hôtel en bord de plage. Très confortable, on y trouve restaurant et piscine, dans un grand jardin.', 'flores-landscape.jpg'),
(511, 12, 'JOUR 12 – LABUAN BAJO', 'Journée libre à Labuan Bajo pour profiter de l’hôtel et de la petite bourgade.', 'Nuit à l’hôtel', 'labuan-bajo-market.JPG'),
(512, 12, 'JOUR 13 – PARC NATIONAL DE KOMODO : randonnée, dragons & Snorkeling', 'Départ pour le port, à bord d’un bateau traditionnel, vous atteindrez l’île de RINCA où vous débuterez votre randonnée. C\'est l\'occasion d\'admirer la faune et la flore endémique de la région, dont les fameux dragons de Komodo.\r\n\r\nNavigation à travers le très beau Parc National de Komodo et ses îles, et DÉJEUNER à bord.\r\n\r\nPrès de l’île de KELOR, arrêt pour la baignade et le Snorkeling.\r\nPuis retour à Labuan Bajo. \r\n', 'Nuit à l’hôtel', 'komodo-9.jpg'),
(513, 12, 'JOUR 14 – LABUAN BAJO', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert à l’aéroport de Labuan Bajo.\r\n', NULL, 'plane-4.jpg'),
(551, 13, 'JOUR 1 – PANGKALAN BUN : Arrivée à Kalimantan', 'Arrivée à l’aéroport de PANGKALANBUN et transfert à votre hôtel.', 'Nuit à l’hôtel', 'jungle-kalimantan.jpg'),
(552, 13, 'JOUR 2 – De PANGKALAN BUN à TANJUNG PUTING', 'Départ pour l’embarcadère où commence votre croisière dans la jungle.\r\nNote : Bateau au confort très simple, le seul moyen de profiter de cette région reculée. \r\n\r\nNAVIGATION à la découverte de la faune et de la flore de la rivière SEKONYER. Vous passerez par le camp de TANJUNG HARAPAN, espace de protection des orang-outans. \r\n', 'Repas et nuit sur le BATEAU', 'river-kalimantan-3.jpg'),
(553, 13, 'JOUR 3 – Dans le PARC NATIONAL de TANJUNG PUTING', 'Journée d’exploration du parc national et sa forêt primaire, sa flore et sa faune. Observation depuis le bateau mais aussi à travers la jungle. \r\n\r\nVous pourrez repérer les orang-outans dans les branches, des singes nasiques, des gibbons, des macaques, plus de 200 d’espèces d’oiseaux. D’autres animaux y vivent aussi, comme les crocodiles, les sangliers sauvages et de nombreux reptiles. \r\n', 'Repas et nuit sur le BATEAU', 'orang-outang-kalimantan.jpg'),
(554, 13, 'JOUR 4 – Dans le PARC NATIONAL de TANJUNG PUTING', 'NAVIGATION vers le village de PESALAT. Vous y planterez un arbre, cette contribution est demandée à chaque visiteur pour limiter les changements climatiques dus à la déforestation.\r\n\r\nVous repasserez par le camp de TANJUNG HARAPAN avant de continuer votre croisière jusqu’à NIPAH. \r\n\r\nVous êtes toujours en plein cœur de la jungle, et après les primates que vous aurez à nouveau pu observer, vous aurez la chance de vivre la plus belle des illuminations féériques naturelles : le bal des lucioles dans les arbres !\r\n', 'Repas et nuit sur le BATEAU', 'river-kalimantan.jpg');
INSERT INTO `days` (`id`, `tours_id`, `title`, `description`, `hotel`, `photo`) VALUES
(555, 13, 'JOUR 5 – DÉPART', 'Dernier moment sur le bateau, pour profiter de cette nature luxuriante, avant de revenir à l’embarcadère.\r\n\r\nPuis transfert vers l’aéroport de PANGKALANBUN et envol vers votre prochaine destination.\r\n', NULL, 'plane-2.jpg'),
(601, 14, 'JOUR 1 – De DENPASAR – à UBUD : arrivée à Bali ', 'Accueil fleuri et personnalisé à l\'aéroport et transfert avec votre guide à UBUD. \r\n\r\n<strong>Cadeau Honeymmoon : deux sarongs traditionnels balinais, à porter pour la visite des temples</strong>\r\n', 'Nuit en hôtel charme avec jardins et piscine, situé à 10 min. à pied de la forêt des singes. ', 'ubud-3.jpg'),
(602, 14, 'JOUR 2 – UBUD : la campagne à vélo', 'Balade à VÉLO à travers les rizières et village pour découvrir la vie quotidienne des habitants. \r\nDéjeuner dans le palais d’un prince balinais. \r\nExcursion au célèbre site de TANAH LOT pour le coucher du soleil.\r\n', 'Nuit à l’hôtel', 'tanah-lot.jpg'),
(603, 14, 'JOUR 3 – UBUD : balade en ville', 'Journée libre à Ubud pour faire connaissance avec la capitale culturelle de l’île : se balader dans la forêt des singes, découvrir les musées et galeries d\'art, assister à un spectacle de danse, goûter à la gastronomie locale, etc. ', 'Nuit à l’hôtel', 'ubud-4.jpg'),
(604, 14, 'JOUR 4 – D’UBUD à MENJANGAN : la région des lacs', 'Journée de découverte du centre et Nord de BALI : du temple TAMAN AYUN, aux rizières en terrasses de JATILUWIH, en passant par la région des lacs et des plantations. \r\n\r\n', 'Nuit dans un superbe éco-lodge sur la plage, au cœur du parc national de Bali Ouest, où vous pourrez observer les animaux dans leur environnement naturel. ', 'jatiluwih.jpg'),
(605, 14, 'JOURS 5 & 6 – MENJANGAN : le Parc National de Bali Ouest', 'Séjour libre pour la baignade et le snorkeling dans ce cadre de rêve.\r\nVous pourrez plonger en apnée depuis le ponton de l’hôtel et découvrir de nombreuses espèces de poissons multicolores.\r\n\r\n<strong>Cadeau Honeymmoon :  Un MASSAGE de 60 minutes vous sera offert au spa de l’hôte ainsi qu’une balade de 2 heures dans le Parc National sous la conduite d’un guide.</strong>\r\n', 'Nuit à l’hôtel', 'pemuteran-biorock.jpg'),
(606, 14, 'JOUR 7 – De MENJANGAN à SAMBANGAN : la région du Nord', 'Départ pour le petit village de Sambangan, arrêt au temple de PULAKI peuplé de singes et balade au marché coloré de SERIRIT. \r\n\r\nArrivée au village et départ pour une superbe randonnée à travers la jungle et les CASCADES où vous pourrez vous baigner. \r\n\r\n', 'Nuit dans un petit hôtel de charme dont les bungalows en Joglo sont disséminés face à un panorama à couper le souffle. ', 'waterfall-bali.jpg'),
(608, 14, 'JOUR 8 – De SAMBANGAN à SIDEMEN : volcans et temple sacré', 'Départ pour la région des volcans et superbe vue panoramique sur le volcan BATUR et le lac.\r\nVisite du temple-mère de BESAKIH, adossé au MONT AGUNG, volcan vénéré par les balinais. \r\n\r\nArrivée en fin d’après-midi dans la belle vallée de SIDEMEN.\r\n\r\n<strong>Cadeau Honeymmoon : chambre décorée de fleurs, un bain de fleurs et une corbeille de fruits, un gâteau Honeymoon ; un massage des pieds.</strong>\r\n\r\n', '\r\nNuit dans un hôtel intimiste avec piscines, spa et vue magnifique sur la vallée et le volcan. Un petit paradis où le temps s\'est arrêté ! \r\n', 'agung-2.jpg'),
(609, 14, 'JOUR 9 – SIDEMEN : immersion culturelle', 'Le matin, vous irez sur le marché chercher les ingrédients nécessaires pour votre COURS DE CUISINE avec le chef balinais.\r\nDéjeuner avec ce que vous aurez préparé\r\n\r\nVisite de la fondation culturelle PASRAMAN Vidja Giri : les enfants vous accueillent pour un cours d’arts balinais ; dans une démarche de tourisme solidaire, une donation sera reversée à la fondation grâce à votre visite.\r\n\r\nAfternoon tea servi face à la vallée.\r\n', 'Nuit à l’hôtel.', 'cooking-class-1.jpg'),
(610, 14, 'JOUR 10 – SIDEMEN : plage et temple', 'Journée baignade sur la plage de PASIR PUTIH et Déjeuner de poissons grillés.\r\nAu retour, visite des bains royaux de TIRTAGGANGA.\r\n\r\n<strong>Cadeau Honeymmoon : un soin au SPA, gommage et MASSAGE suivis d’un BAIN DE FLEURS.\r\nEn soirée, DÎNER ROMANTIQUE dans les jardins de l’hôtel, des habits balinais vous seront prêtés pour la soirée. </strong>\r\n', 'Nuit à l’hôtel.', 'massage-bali-2.jpg'),
(611, 14, 'JOUR 11 – DÉPART', 'Chambre à disposition jusqu\'à midi.\r\nSelon les horaires de votre vol, transfert à l’aéroport et assistance à l’enregistrement.\r\n', NULL, 'plane-5.jpg');
